'      if p_optgroups(l_index) = p_optgroup then',
'        return true;',
'      end if;',
'',
'      l_index := p_optgroups.next(l_index);',
'    end loop;',
'',
'    return false;',
'  end optgroup_exists;',
'',
'',
'  function is_selected_value(',
