'    p_param    IN apex_plugin.t_item_render_param,',
'    p_result   IN OUT NOCOPY apex_plugin.t_item_render_result',
')',
'  --RETURN apex_plugin.t_page_item_render_result ',
' IS',
'',
'    l_result                     apex_plugin.t_page_item_render_result;',
'    l_name                       VARCHAR2(30);',
'    l_escaped_value              VARCHAR2(1000);',
'    l_html_string                VARCHAR2(2000);',
'    l_data_string                VARCHAR2(2000);',
'    l_element_item_id            VARCHAR2(200);',
'    l_element_item_classes       VARCHAR2(200);',
'    l_element_item_placeholder   VARCHAR2(200);',
'    l_element_item_width         VARCHAR2(200) := 200;',
'    c                            SYS_REFCURSOR;',
'    json_data                    CLOB;',
'    l_title                      VARCHAR2(200);',
'    l_browser_language           VARCHAR2(10);',
'    ----------------',
'    l_disable_past_month         VARCHAR2(50) := p_item.attribute_01;',
'    l_max_month                  VARCHAR2(50) := p_item.attribute_02;',
'    l_start_year                 VARCHAR2(50) := p_item.attribute_03;',
'    l_start_year_value           VARCHAR2(50) := p_item.attribute_04;',
'    l_float_icon_position        VARCHAR2(50) := p_item.attribute_05;',
' ',
'  --',
'BEGIN',
'    l_element_item_id := p_item.name;',
'    l_element_item_classes := p_item.element_css_classes;',
'    l_element_item_placeholder := p_item.placeholder;',
'    l_element_item_width := p_item.element_width;',
'    l_element_item_id := p_item.name;',
'    l_name := apex_plugin.get_input_name_for_page_item(false);',
'    l_escaped_value := apex_escape.html(p_param.value);',
'    l_browser_language := v(''BROWSER_LANGUAGE'');  ',
'    --',
'    l_html_string := ''<input '';',
'    l_html_string := l_html_string || ''type="text" '';',
'    l_html_string := l_html_string',
'     || ''placeholder="''',
'     || l_element_item_placeholder',
'     || ''" '';',
'    l_html_string := l_html_string',
'     || ''id="''',
'     || l_element_item_id',
'     || ''" '';',
'    l_html_string := l_html_string',
'     || ''class="''',
'     || l_element_item_classes',
'     || '' text_field apex-item-text ''',
'     || ''" '';',
'    l_html_string := l_html_string',
'     || ''name="''',
'     || l_name',
'     || ''" '';',
'    l_html_string := l_html_string',
'     || ''value="''',
'     || l_escaped_value',
'     || ''" '';',
'    l_html_string := l_html_string',
'     || ''size="''',
'     || l_element_item_width',
'     || ''" '';',
'    l_html_string := l_html_string || '' />'';',
'    sys.htp.prn(l_html_string);',
'    apex_debug.message(l_html_string);',
'  ',
'    -- Initialize the Picker',
'    apex_javascript.add_onload_code(''$(document).ready(function(){ month_year.init({''',
'     || ''"element_item_classes" : "''',
'     || l_element_item_classes',
'     || ''"''',
'     || '',"element_item_id" : "''',
'     || l_element_item_id',
'     || ''"''',
'     || '',"browser_language" : "''',
'     || l_browser_language',
'     || ''"''',
'     || '',"disable_past_month" : "''',
'     || l_disable_past_month',
'     || ''"''',
'     || '',"max_month" : "''',
'     || l_max_month',
'     || ''"''',
'     || '',"start_year" : "''',
'     || l_start_year',
'     || ''"''',
'     || '',"start_year_value" : "''',
'     || l_start_year_value',
'     || ''"''',
'     || '',"float_icon_position" : "''',
'     || l_float_icon_position',
'     || ''"''',
'     || ''}); });'');',
'    --l_result.is_navigable := TRUE;',
'  --',
'  --RETURN(l_result);',
'',
'END render_month_year;'))
,p_api_version=>2
,p_render_function=>'render_month_year'
,p_standard_attributes=>'VISIBLE:SESSION_STATE:READONLY:ESCAPE_OUTPUT:SOURCE:ELEMENT:WIDTH:ELEMENT_OPTION:PLACEHOLDER'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>'APEX-Month-Year-Picker Item Plugin is designed to allow user input for only a month and year when only that input is required. Clicking on the year, allows the user to jump ahead or back 12 years at a time. Clicking anywhere on the page, except on th'
||'e month picker menu itself, will cause the month picker to hide. The Month Picker has lots of options for date validation, setting the start year, using an icon button, input masking, internationalization, localization and more.'
,p_version_identifier=>'1.1'
,p_about_url=>'https://github.com/allipierre/APEX-Month-Year-Picker'
